public class Daily20200727 {


    public static void main(String[] args) {
        String s = "leeeeetcode";
        String t = "";
        System.out.println(isSubsequence(s,t));
    }

    public static  boolean isSubsequence(String s, String t) {
        if(t.isEmpty() && s.isEmpty()){
            return true;
        } else if(t.isEmpty()){
            return false;
        }
        Integer flag = -1;
        for(Character character : s.toCharArray()){
            Integer point = null;
            if(flag == -1){
                point = t.indexOf(character);
            } else {
                point = t.indexOf(character, flag+1);
            }
           if(point > -1 && point > flag){
               flag = point;
           } else {
               return false;
           }
        }
        return true;
    }
}
